INSERT INTO servers (mcp_name, mcp_description, provider_name, provider_is_official, server_type, hosting_type, tools_count, tools_definitions_json, repo_full_name, repo_stargazers_count, metadata_is_active) VALUES ('Context7 MCP Server by Upstash', 'The Context7 MCP Server enables AI assistants and code editors to retrieve the latest, library-specific code documentation and code examples from Context7, letting users get accurate, current programming information for any prompt or coding task inside their IDE or AI client.', 'Upstash, Inc.', true, 'LOCAL_STDIO', 'EXTERNAL', 2, '[{"desc":"Resolves an arbitrary package/library name into one or more Context7-compatible library IDs by searching Context7â€™s documentation index. Returns a ranked list of plausible matches, each with title, unique Context7 ID, latest version, snippet count, and popularity (GitHub stars). Use this tool first, before requesting documentation for any library.","name":"resolve-library-id","func_type":"SEARCH_QUERY","risk_score":5,"args_schema":{"libraryName":{"type":"string","required":true,"description":"The package, module, or library name to search (e.g., 'next', 'react', 'lodash'). Can be partial or ambiguous; server will return best matches."}},"friendly_name":"Resolve Library Identifier","output_schema":{"results":{"type":"array","items":{"type":"object","properties":{"id":{"type":"string","description":"Context7-compatible library ID (slash path, e.g., '/vercel/next.js')."},"stars":{"type":"integer","description":"GitHub star count for this library's repo."},"state":{"enum":["initial","finalized","error","delete"],"type":"string","description":"Indexing/finalization status of the docs for this library."},"title":{"type":"string","description":"Human-readable name of the library."},"branch":{"type":"string","description":"Version branch (e.g., 'main', 'master', or a tag/semver)."},"lastUpdate":{"type":"string","description":"ISO8601 date/time string of the last automatic documentation update for this library."},"totalPages":{"type":"integer","description":"Documentation section/page count for this library."},"description":{"type":["string","null"],"description":"Short library summary, if available."},"totalTokens":{"type":"integer","description":"Total number of documentation tokens indexed for this library."},"totalSnippets":{"type":"integer","description":"Total number of code snippets extracted for this library."}}},"description":"Array of matching libraries with detailed identifiers and info."}},"mapped_category":["library_id_resolution"]},{"desc":"Fetches the latest documentation and code examples for a chosen library by Context7-compatible ID (as returned by 'resolve-library-id'). Optionally focus on a topic (e.g., 'routing', 'hooks'), and control the maximum number of tokens returned. Always call 'resolve-library-id' first to ensure you have a valid library ID.","name":"get-library-docs","func_type":"DATA_READ","risk_score":5,"args_schema":{"topic":{"type":"string","required":false,"description":"Specific topic within the library to focus the documentation on (e.g., 'routing', 'hooks', 'api reference'). If not specified, returns general/core docs."},"tokens":{"type":"integer","default":10000,"required":false,"description":"Maximum number of documentation tokens to return (default and enforced minimum: 10000). If lower, it will be increased to 10000 for meaningful context."},"context7CompatibleLibraryID":{"type":"string","required":true,"description":"Exact Context7-compatible library ID (slash path format, e.g., 'vercel/next.js') as returned by 'resolve-library-id'."}},"friendly_name":"Get Library Documentation","output_schema":{"content":{"type":"array","items":{"type":"object","properties":{"text":{"type":"string","description":"The actual documentation content, or an error message if docs are unavailable."},"type":{"type":"string","description":"Always 'text'; may extend to richer result types in future."}}},"description":"Array containing documentation for the library. If found, will include rich plain text. If not found, provides a fallback error message."}},"mapped_category":["documentation_retrieval"]}]'::jsonb, 'upstash/context7', 8455, true) RETURNING id, mcp_name;